mapper.select:
SELECT COUNT(*)
FROM USING_USER

WHERE USER_PW = #{user_pw}
     AND USER_ID = #{user_id}

SELECT COUNT(*) AS COUNT
FROM USING_USER
WHERE USER_NICKNAME = #{user_nick}

SELECT COUNT(*) AS COUNT
FROM USER_LIST
WHERE USER_NUM = #{user_num}

SELECT COUNT(*) AS COUNT
FROM STORE_VIEW
WHERE USER_NUM = #{user_num}

SELECT USER_NICKNAME, USER_NUM, USER_ID
FROM USER_VIEW

WHERE USER_ID = #{user_info}


WHERE USER_NUM = #{user_info}

SELECT USER_GRADE, POINT_SUM
FROM FIRST_HALF
WHERE USER_NUM = #{user_num}

SELECT USER_GRADE, POINT_SUM
FROM SECOND_HALF
WHERE USER_NUM = #{user_num}

SELECT USER_NUM, USER_NAME, USER_SSN1, USER_SSN2, USER_ID, USER_TEL
FROM USING_USER
WHERE USER_NUM = #{user_num}

SELECT COUNT(*) AS COUNT
FROM USING_USER
WHERE USER_NICKNAME = #{nickName}

mapper.update:
UPDATE USING_USER
SET USER_PW = #{user_pw},
    USER_NICKNAME=#{user_nickname},
    USER_EMAIL=#{user_email}
WHERE USER_NUM = #{user_num}
